mapper.select:
select avg(rating_score)
from moons_rating
where rating_movie=#{movie}

select count(*)
from moons_rating
where user_code=#{user_code} and rating_movie=#{rating_movie}

select rating_score
from moons_rating
where user_code=#{user_code} and rating_movie=#{rating_movie}

mapper.update:
update moons_rating
set	rating_score=#{rating_score}
where user_code=#{user_code} and rating_movie=#{rating_movie}

mapper.select:
select case when count(*) > 0 then 1 else 0 end
from moons_rating
where rating_movie=#{movie}
